import React, { Component } from "react";
import "./Style.css";
import data from "./data.json"
//import { SocialIcon } from "react-social-icons";
import FullpageDiv from "./FullpageDiv";
//import "./Sections.css";
//import { Element } from "react-scroll";

class SecondSection extends Component {

 Bgover = (color) => {
    document.getElementById("para").style.backgroundColor= color;
  }
 Bgout = () => {
    document.getElementById("para").style.backgroundColor= "red";
  }


  render() {
    return (
      <FullpageDiv id="SecondSection" className="Content">
        <div className="MainText2">
          <p className="Paragraph" id="para" onMouseOver={() => {this.Bgover();}} onMouseOut={() => {this.Bgout();}}>
            {data.about}
          </p>
          <p className="ParagraphEdu">
            {data.education1}
            <br />
            {data.education2}
          </p>
        </div>

              
      </FullpageDiv>
    );
  }
}

export default SecondSection;
