import React, { Component } from "react";
import "./Style.css";
//import data from "../data.json";
//import { SocialIcon } from "react-social-icons";
import FullpageDiv from "./FullpageDiv";
//import { Element } from "react-scroll";

class FirstSection extends Component {
   
  componentDidMount() {
    function changeBubble() {
        var scrollY = window.scrollY;
        var windowHeight = window.innerHeight;
        var firstPageHeight = document.getElementById('firstPage').offsetHeight;
        var secondPageOffsetTop = document.getElementById('secondPage').offsetTop;
        var secondPageHeight = document.getElementById('secondPage').offsetHeight;
        var bubble = document.getElementById('LeftBubble');

        // Calculate the scale factor and position based on scroll position
        var scaleFactor = Math.min((scrollY + windowHeight) / firstPageHeight, 1.7);
        var newPosition = (scrollY + windowHeight >= secondPageOffsetTop) ? 'center' : 'left';

        // Apply scale factor
        bubble.style.width = (12 * scaleFactor) + 'vw';
        bubble.style.height = (12 * scaleFactor) + 'vw';

        // Apply position
        bubble.style.left = newPosition === 'center' ? '50%' : '-7vw';
        bubble.style.transform = newPosition === 'center' ? 'translateX(-50%)' : 'none';
        bubble.style.top = newPosition === 'center' ? (secondPageOffsetTop + secondPageHeight / 2) + 'px' : '7vw';
    }

    window.addEventListener('scroll', function () {
        requestAnimationFrame(changeBubble);
    });

    // Initial call to set bubble size and position
    changeBubble();
}
  //console.log("Script file is working");
  

  render() {
    return (
      <FullpageDiv id="FirstSection" className="Content">
      <div className="TopCircle"></div>
        <div className="Header"> 
            <div className="LeftHeader">Shayan Ebrahimi</div>
            <div className="RightHeader">
              <ul>
                <li><a>About</a></li>
                <li><a>Skills</a></li>
                <li><a>Contact</a></li>
              </ul>
            </div>
        </div>
        <div className="LeftBubble" id="LeftBubble"></div>
        <div className="RightBubble"></div>
        <div className="BigBubble"></div>
        <div className="BottomCircle"></div>
        <div className="MainText">
          <p>
            My Name Is Shayan
          </p>
          <p>
            Full Stack Web | Php & MySql | Ui / UX <br/>React | HTML | CSS
          </p>
        </div>
        <script></script>
      </FullpageDiv>
    );
  }
}

export default FirstSection;
