import React, { Component } from "react";
import "./Style.css";
import data from "./data.json"
//import { SocialIcon } from "react-social-icons";
import FullpageDiv from "./FullpageDiv";
//import "./Sections.css";
//import { Element } from "react-scroll";

class SecondSection extends Component {
componentDidMount () {

  function Bgover() {
    document.getElementById("para").backgroundColor= "red";
  }
  function Bgout() {
    document.getElementById("para").backgroundColor= "white";
  }
  
}

  render() {
    return (
      <FullpageDiv id="SecondSection" className="Content">
        <div className="MainText2">
          <p className="Paragraph" id="para" onMouseOver={Component.Bgover} onMouseOut={Component.Bgout}>
            {data.about}
          </p>
          <p className="ParagraphEdu">
            {data.education1}
            <br />
            {data.education2}
          </p>
        </div>

              
      </FullpageDiv>
    );
  }
}

export default SecondSection;
