import React, { Component } from "react";
import "./Style.css";
//import data from "../data.json";
//import { SocialIcon } from "react-social-icons";
import FullpageDiv from "./FullpageDiv";
//import { Element } from "react-scroll";

class FirstSection extends Component {
   
  componentDidMount() {
    document.getElementById('LeftBubble').style.top = '7vw';

    function changeWidth() {
        var scroll = window.pageYOffset + window.innerHeight; // Current scroll position plus viewport height
        var firstPageHeight = document.getElementById('FirstSection').offsetHeight; // Height of the first full-page div
        var secondPageOffset = document.getElementById('SecondSection').offsetTop; // Offset of the second full-page div from the top

        var maxBubbleSize = 25; // Maximum size of the bubble in vw
        var minBubbleSize = 12; // Minimum size of the bubble in vw

        // Calculate the size of the bubble based on scroll position
        var width = minBubbleSize + ((scroll / firstPageHeight) * (maxBubbleSize - minBubbleSize) * 1.7);

        // Set the width and height of the bubble
        document.getElementById('LeftBubble').style.width = width + 'vw';
        document.getElementById('LeftBubble').style.height = width + 'vw';

        // Move the bubble to the center of the second full-page div when it reaches a certain size
        if (width >= (maxBubbleSize * 1.7)) {
            document.getElementById('LeftBubble').style.left = 'calc(50% - ' + (width / 2) + 'vw)';
            document.getElementById('LeftBubble').style.top = secondPageOffset + 'px';
        }

        // Adjust other styles as needed
        if (width >= 7) {
            document.getElementById('LeftBubble').style.marginTop = (width * 0.85) + 'vw';
            document.getElementById('LeftBubble').style.left = -(width * 0.1) + 'vw';
        } else {
            document.getElementById('LeftBubble').style.marginTop = '6vw';
        }
    }

    window.addEventListener('scroll', function () {
        requestAnimationFrame(changeWidth);
    });
}

  
  //console.log("Script file is working");
  

  render() {
    return (
      <FullpageDiv id="FirstSection" className="Content">
      <div className="TopCircle"></div>
        <div className="Header"> 
            <div className="LeftHeader">Shayan Ebrahimi</div>
            <div className="RightHeader">
              <ul>
                <li><a>About</a></li>
                <li><a>Skills</a></li>
                <li><a>Contact</a></li>
              </ul>
            </div>
        </div>
        <div className="LeftBubble" id="LeftBubble"></div>
        <div className="RightBubble"></div>
        <div className="BigBubble"></div>
        <div className="BottomCircle"></div>
        <div className="MainText">
          <p>
            My Name Is Shayan
          </p>
          <p>
            Full Stack Web | Php & MySql | Ui / UX <br/>React | HTML | CSS
          </p>
        </div>
        <script></script>
      </FullpageDiv>
    );
  }
}

export default FirstSection;
