import React, { Component } from "react";
import "./Style.css";
import data from "./data.json"
//import { SocialIcon } from "react-social-icons";
import FullpageDiv from "./FullpageDiv";
//import { Element } from "react-scroll";
import { FaLinkedinIn  , FaInstagram , FaFacebook  } from 'react-icons/fa';
import { AiOutlineMail  } from 'react-icons/ai';

import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from 'react-responsive-carousel';

class FirstSection extends Component {

  MouseOver = (id,decoration) => {
    document.getElementById(id).style.textDecorationLine=decoration;
  }

    componentDidMount () {

      const responsive = {
        desktop: {
          breakpoint: { max: 3000, min: 1024 },
          items: 3,
          slidesToSlide: 3 // optional, default to 1.
        },
        tablet: {
          breakpoint: { max: 1024, min: 464 },
          items: 2,
          slidesToSlide: 2 // optional, default to 1.
        },
        mobile: {
          breakpoint: { max: 464, min: 0 },
          items: 1,
          slidesToSlide: 1 // optional, default to 1.
        }
      };

      function changeWidth() {
        var scroll = (window.pageYOffset) + ((window.pageYOffset) * 0.07);
        var width = Math.min(scroll / 11.5);
        var newWindowHeight = window.innerHeight;

        document.getElementById('LeftBubble').style.width = width + 'vw';
        document.getElementById('LeftBubble').style.height = width + 'vw';
    
        if (width >= 7) {
          if(newWindowHeight <= 1000)
          {
            document.getElementById('LeftBubble').style.marginTop = (width * 1.5) + 'vw';
          }else if(newWindowHeight <= 1500){
            document.getElementById('LeftBubble').style.marginTop = (width * 1) + 'vw';
          }
          else{
            document.getElementById('LeftBubble').style.marginTop = (width * 0.45) + 'vw';
          }
            document.getElementById('LeftBubble').style.left = '-8vw'; // Maintain the original left position
        } else {
            document.getElementById('LeftBubble').style.marginTop = '7vw';
        }
    }
    
    window.addEventListener('scroll', function () {
        requestAnimationFrame(changeWidth);
    });
    changeWidth();
  }


  
  render() {
    return (
      
      <FullpageDiv id="FirstSection" className="Content">
      <div className="TopCircle"></div>
        <div className="Header"> 
            <div className="LeftHeader">Shayan Ebrahimi</div>
            <div className="RightHeader">
              <ul>
                <li><a id="about" onMouseOver={() => {this.MouseOver("about","underline");}} onMouseOut={() => {this.MouseOver("about","");}} >About</a></li>
                <li><a id="skills" onMouseOver={() => {this.MouseOver("skills","underline");}} onMouseOut={() => {this.MouseOver("skills","");}}>Skills</a></li>
                <li><a id="contact" onMouseOver={() => {this.MouseOver("contact","underline");}} onMouseOut={() => {this.MouseOver("contact","");}}>Contact</a></li>
              </ul>
            </div>
        </div>
        <div className="LeftBubble" id="LeftBubble"></div>
        <div className="RightBubble"></div>
        <div className="BigBubble">
          <div className="Contact">
          <Carousel
            swipeable={false}
            draggable={false}
            showDots={true}
            //responsive={responsive}
            ssr={true} // means to render carousel on server-side.
            infinite={true}
            autoPlay={this.props.deviceType !== "mobile" ? true : false}
            autoPlaySpeed={1000}
            keyBoardControl={true}
            customTransition="all .5"
            transitionDuration={500}
            containerClass="carousel-container"
            removeArrowOnDeviceType={["tablet", "mobile"]}
            deviceType={this.props.deviceType}
            dotListClass="custom-dot-list-style"
            itemClass="carousel-item-padding-40-px"
          >
            <div className="ContactIcons"><FaInstagram  /></div>
            <div className="ContactIcons"><FaLinkedinIn  /></div>
            <div className="ContactIcons"><FaFacebook  /></div>
            <div className="ContactIcons"><AiOutlineMail  /></div>
            </Carousel>
          </div>
        </div>
        <div className="BottomCircle"></div>
        <div className="MainText">
          <p>
            {data.title}
          </p>
          <p>
            {data.subtitle}
          </p>
        </div>
        <script></script>
      </FullpageDiv>
    );
  }
}

export default FirstSection;
