import React, { Component } from "react";
import "./Style.css";
import data from "./data.json"
import { TypeAnimation } from 'react-type-animation';

//import { SocialIcon } from "react-social-icons";
import FullpageDiv from "./FullpageDiv";
//import "./Sections.css";
//import { Element } from "react-scroll";

class SecondSection extends Component {

 Bgover = (id,color) => {
    document.getElementById(id).style.backgroundColor= color;
  }


  render() {
    return (
      <FullpageDiv id="SecondSection" className="Content">
        <div className="MainText2">
          <p className="Paragraph" id="para" onMouseOver={() => {this.Bgover("para","yellow");}} onMouseOut={() => {this.Bgover("para","");}}>
            {data.about}
          </p>

          <br />
          <br />
          <br />

          <p className="ParagraphEdu">
            {data.education1} <label className="uni">{data.uni1}</label>
            <br />
            {data.education2} <label className="uni">{data.uni2}</label>
          </p>
        </div>

              
      </FullpageDiv>
    );
  }
}

export default SecondSection;
