import React, { Component } from "react";
import "./Style.css";
import data from "./data.json"
//import { SocialIcon } from "react-social-icons";
import FullpageDiv from "./FullpageDiv";
//import "./Sections.css";
//import { Element } from "react-scroll";
import SkillBox from "./SkillBox";

class ThirdSection extends Component {

  Bgover = (id,color) => {
    document.getElementById(id).style.Color= color;
  }

  render() {
    return (
      <FullpageDiv id="ThirdSection" className="Content3">

        <h2 className="SectionsTitle">{data.sections[0].title}</h2>
        <div className="SkilksContainer">
            {data.sections[0].items.map((task) => {
              return [
                <SkillBox skill={task}/>
              ];
            })}
        </div>
        <div className="Copy">
        Code, design and copy by: <lable className="" id="me" onMouseOver={() => {this.Bgover("me","");}} onMouseOut={() => {this.Bgover("me","#e4e3e3");}}> ME !</lable>
        </div>      
      </FullpageDiv>
    );
  }
}

export default ThirdSection;
